package org.cargo.command.domain;

import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.GeneratedValue;
import javax.persistence.GenerationType;
import javax.persistence.Id;
import javax.persistence.Table;
import org.sculptor.framework.domain.AbstractDomainObject;

/**
 * Uniquely identifies a particular cargo. Automatically generated by the application.
 */

@Entity
@Table(name = "TRACKINGID")
public class TrackingID extends AbstractDomainObject {

	private static final long serialVersionUID = 1L;

	@Id
	@GeneratedValue(strategy = GenerationType.AUTO)
	@Column(name = "")
	private String id;

	public TrackingID() {
	}

	public String getId() {
		return id;
	}

	/**
		* The id is not intended to be changed or assigned manually, but
		* for test purpose it is allowed to assign the id.
		*/
	protected void setId(String id) {
		if ((this.id != null) && !this.id.equals(id)) {
			throw new IllegalArgumentException("Not allowed to change the id property.");
		}
		this.id = id;
	}

	/**
	 * This method is used by equals and hashCode.
	 * @return {@link #getId}
	 */
	public Object getKey() {
		return getId();
	}

}
